import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './styles/StudentDashboard.css';

function StudentDashboard() {
  const [files, setFiles] = useState(Array(10).fill({ name: '', file: null }));
  const { email } = useParams();
  const [student, setStudent] = useState({});
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleFileChange = (index, e) => {
    const updatedFiles = [...files];
    updatedFiles[index] = { ...updatedFiles[index], file: e.target.files[0] };
    setFiles(updatedFiles);
  };

  const handleNameChange = (index, e) => {
    const updatedFiles = [...files];
    updatedFiles[index] = { ...updatedFiles[index], name: e.target.value };
    setFiles(updatedFiles);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    setIsUploading(true);
    setErrorMessage('');

    if (!email) {
      alert('Email is required!');
      setIsUploading(false);
      return;
    }

    const uploadPromises = files.map(async (fileObj, index) => {
      if (fileObj.file && fileObj.name) {
        const formData = new FormData();
        formData.append('file', fileObj.file);
        formData.append('name', fileObj.name);
        formData.append('email', email);

        try {
          const response = await axios.post('http://localhost:8080/upload', formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
          });
          return response.data;
        } catch (error) {
          console.error(`Error uploading file ${index + 1}:`, error);
          return null;
        }
      }
      return null;
    });

    const results = await Promise.all(uploadPromises);
    const successfulUploads = results.filter((result) => result !== null);

    if (successfulUploads.length > 0) {
      alert('Files uploaded successfully!');
      fetchFiles();
    } else {
      setErrorMessage('Some files failed to upload. Please try again.');
    }
    
    setIsUploading(false);
  };

  const fetchFiles = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/files/${email}`);
      setUploadedFiles(response.data);
    } catch (error) {
      console.error('Error fetching uploaded files:', error);
    }
  }, [email]);

  const fetchStudentDetails = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
      setStudent(response.data.student);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  }, [email]);

  useEffect(() => {
    if (email) {
      fetchFiles();
      fetchStudentDetails();
    }
  }, [email, fetchFiles, fetchStudentDetails]);

  return (
    <div className="dashboard-container">
      <h2>Welcome back, {student.name || 'Student'}!</h2>
      <p>Upload your certificates - Get Credit Points.</p>
      <p>Total Marks: {student.marks || 0}</p>

      {errorMessage && <p className="error-message">{errorMessage}</p>}
      
      <form onSubmit={handleUpload} className="upload-form">
        {files.map((fileObj, index) => (
          <div key={index} className="file-upload-group">
            <label>
              File {index + 1} Name:
              <input
                type="text"
                value={fileObj.name}
                onChange={(e) => handleNameChange(index, e)}
                placeholder="Enter file name"
                required
              />
            </label>

            <label>
              Upload File:
              <input
                type="file"
                onChange={(e) => handleFileChange(index, e)}
                required
              />
            </label>
          </div>
        ))}

        <button type="submit" disabled={isUploading}>
          {isUploading ? 'Uploading...' : 'Upload Files'}
        </button>
      </form>

      <h3>Your Uploaded Files</h3>
      <ul>
        {uploadedFiles.length > 0 ? (
          uploadedFiles.map((file) => (
            <li key={file._id}>
              <a href={`http://localhost:8080/uploads/${file.filename}`} target="_blank" rel="noopener noreferrer">
                {file.originalname} ({file.name})
              </a>
            </li>
          ))
        ) : (
          <li>No files uploaded yet.</li>
        )}
      </ul>
    </div>
  );
}

export default StudentDashboard;
