import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {  handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import axios from 'axios';

function StudentDashboard({ loggedInEmail }) {
  const [file, setFile] = useState(null); // Define 'file' in the state
  

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };
  
  
  

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file first!');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', loggedInEmail); // Add the email to the form data
 

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert(response.data.message);
      console.log(response.data);
    }catch (error) {
      // Add more detailed logging to understand the error
      if (error.response) {
        console.error('Error response:', error.response.data); // Log the error response data
        console.error('Error status:', error.response.status); // Log the status code
      } else {
        console.error('Error uploading file:', error.message); // Log generic error message
      }
    }
  };
  const [loggedInUser, setLoggedInUser] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

  return (
    <div>
      <h1>Welcome {loggedInUser}</h1>
      <button onClick={handleLogout}>Logout</button>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload File</button>
      </form>
      <ToastContainer />
    </div>
  );
}

export default StudentDashboard;
