import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function ViewUploadedFiles() {
  const [files, setFiles] = useState([]);
  const { email } = useParams();

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        console.log(`Fetching files for email: ${email}`); // Debugging
        const response = await axios.get(`http://localhost:8080/files/${email}`);
        console.log('Fetched files:', response.data); // Debugging
        
        // Log each file's status to verify it exists and matches the filter conditions
        response.data.forEach(file => {
          console.log(`File: ${file.originalname}, Status: ${file.status}`);
        });
  
        setFiles(response.data);
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    };
  
    fetchFiles();
  }, [email]);
  

  if (!files || files.length === 0) {
    return <div className="text-center mt-5">No files available.</div>;
  }

  return (
    <div className="container mt-5">
      <h3 className="text-center text-info">Uploaded Files</h3>

      {/* Pending Files */}
      <div className="mt-4">
        <h4 className="text-warning">Pending Files</h4>
        {files.filter(file => file.status === 'pending').map(file => (
          <div key={file._id} className="my-2">
            <a
              href={`http://localhost:8080/uploads/${file.filename}`}
              target="_blank"
              rel="noopener noreferrer"
              className="btn btn-outline-primary w-100"
            >
              {file.originalname}
            </a>
          </div>
        ))}
        {files.filter(file => file.status === 'pending').length === 0 && (
          <p className="text-center text-muted">No pending files</p>
        )}
      </div>

      {/* Approved Files */}
      <div className="mt-4">
        <h4 className="text-success">Approved Files</h4>
        {files.filter(file => file.status === 'Approved').map(file => (
          <div key={file._id} className="my-2">
            <a
              href={`http://localhost:8080/uploads/${file.filename}`}
              target="_blank"
              rel="noopener noreferrer"
              className="btn btn-outline-success w-100"
            >
              {file.originalname}
            </a>
          </div>
        ))}
        {files.filter(file => file.status === 'Approved').length === 0 && (
          <p className="text-center text-muted">No approved files</p>
        )}
      </div>

      {/* Rejected Files */}
      <div className="mt-4">
        <h4 className="text-danger">Rejected Files</h4>
        {files.filter(file => file.status === 'Rejected').map(file => (
          <div key={file._id} className="my-2">
            <a
              href={`http://localhost:8080/uploads/${file.filename}`}
              target="_blank"
              rel="noopener noreferrer"
              className="btn btn-outline-danger w-100"
            >
              {file.originalname}
            </a>
          </div>
        ))}
        {files.filter(file => file.status === 'Rejected').length === 0 && (
          <p className="text-center text-muted">No rejected files</p>
        )}
      </div>
    </div>
  );
}

export default ViewUploadedFiles;
