import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function VerificationPage() {
    const { studentEmail } = useParams(); // Extract studentEmail from URL
    const [files, setFiles] = useState([]);

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/api/mentor/student/${studentEmail}/files`);
                if (response.data.success) {
                    setFiles(response.data.files);
                }
            } catch (error) {
                console.error('Error fetching files:', error);
            }
        };

        if (studentEmail) fetchFiles();
    }, [studentEmail]);

    const handleApprove = async (fileId) => {
        const marks = prompt("Enter marks for approval:");
        if (!marks) return;

        try {
            await axios.post(`http://localhost:8080/api/mentor/student/${studentEmail}/approve`, { fileId, marks });
            setFiles(files.filter(file => file._id !== fileId)); // Remove approved file from list
        } catch (error) {
            console.error('Error approving file:', error);
        }
    };

    const handleReject = async (fileId) => {
        try {
            await axios.post(`http://localhost:8080/api/mentor/student/${studentEmail}/reject`, { fileId });
            setFiles(files.filter(file => file._id !== fileId)); // Remove rejected file from list
        } catch (error) {
            console.error('Error rejecting file:', error);
        }
    };

    return (
        <div>
            <h2>Verification Page for {studentEmail}</h2>
            {files.length > 0 ? (
                <ul>
                    {files.map((file) => (
                        <li key={file._id}>
                            <p>File Path: {file.filePath}</p>
                            <button onClick={() => handleApprove(file._id)}>Approve</button>
                            <button onClick={() => handleReject(file._id)}>Reject</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No files available for verification.</p>
            )}
        </div>
    );
}

export default VerificationPage;
