import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './styles/StudentDashboard.css';

function StudentDashboard() {
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);
  const { email } = useParams(); // Get email from URL parameters
  const [student, setStudent] = useState({});

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email);

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert(response.data.message);
      fetchFiles(); // Fetch files again after upload
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const fetchFiles = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/files/${email}`);
      setFiles(response.data); // Set the files directly if response.data is an array of files
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  }, [email]);

  const fetchStudentDetails = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
      const studentData = response.data.student;
      setStudent(studentData);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  }, [email]);

  useEffect(() => {
    if (email) {
      fetchFiles();
      fetchStudentDetails();
    }
  }, [email, fetchFiles, fetchStudentDetails]);

  return (
    <div className="dashboard-container">
      <h2>Welcome back, {student.name || 'Student'}!</h2>
      <p>Upload your certificates - Get Credit Points.</p>
      <p>Total Marks: {student.marks || 0}</p>
      
      <form onSubmit={handleUpload} className="upload-form">
        <label>
          File Name:
          <input type="text" placeholder="Enter file name" required />
        </label>
        
        <label>
          Choose file:
          <input type="file" onChange={handleFileChange} required />
        </label>
        
        <button type="submit">Upload File</button>
      </form>
      
      <h3>Your Uploaded Files</h3>
      <ul>
        {files.length > 0 ? (
          files.map(file => (
            <li key={file._id}>
              <a href={`http://localhost:8080/uploads/${file.filename}`} target="_blank" rel="noopener noreferrer">
                {file.originalname}
              </a>
            </li>
          ))
        ) : (
          <li>No files uploaded yet.</li>
        )}
      </ul>
    </div>
  );
}

export default StudentDashboard;
