import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


function MentorDashboard() {
  const [files, setFiles] = useState([]);
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/mentor/students');
        setStudents(response.data);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };

    fetchStudents();
  }, []);
  const handleStudentClick = (email) => {
    navigate(`/mentor/verification/${email}`); // Update this to navigate to the verification page
};



  return (
    <div>
      <h2>Mentor Dashboard</h2>
      <h3>Registered Students</h3>
      <ul>
        {students.map((student) => (
          <li key={student.email} onClick={() => handleStudentClick(student.email)}>
            {student.name} - {student.email}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MentorDashboard;
