import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function ViewUploadedFiles() {
  const [files, setFiles] = useState([]);
  const { email } = useParams();

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/files/${email}`);
        console.log('Files fetched:', response.data); // Debug log
        setFiles(response.data);
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    };

    fetchFiles();
  }, [email]);

  // Check if files array is populated correctly
  if (!files || files.length === 0) {
    return <div className="text-center mt-5">No files available.</div>;
  }

  return (
    <div className="container mt-5">
      <h3 className="text-center text-info">Uploaded Files</h3>

      {['pending', 'approved', 'rejected'].map((status) => (
        <div key={status} className="mt-4">
          <h4 className={`text-${status === 'pending' ? 'warning' : status === 'approved' ? 'success' : 'danger'}`}>
            {status.charAt(0).toUpperCase() + status.slice(1)} Files
          </h4>
          {files.filter(file => file.status === status).map(file => (
            <div key={file._id} className="my-2">
              <a
                href={`http://localhost:8080/uploads/${file.filename}`}
                target="_blank"
                rel="noopener noreferrer"
                className={`btn btn-outline-${status === 'pending' ? 'primary' : status === 'approved' ? 'success' : 'danger'} w-100`}
              >
                {file.originalname}
              </a>
            </div>
          ))}
          {files.filter(file => file.status === status).length === 0 && (
            <p className="text-center text-muted">No {status} files</p>
          )}
        </div>
      ))}
    </div>
  );
}

export default ViewUploadedFiles;
