import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';


function StudentDashboard() {
  const [file, setFile] = useState(null);
  //const [email, setEmail] = useState('');
  const [files, setFiles] = useState([]);
  const { email } = useParams(); // Get email from URL parameters


  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };
  const [student, setStudent] = useState({});
  useEffect(() => {
    const fetchStudentDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/student/details`);
            if (response.data.success) {
              setStudent(response.data.student);

            }
        } catch (error) {
            console.error('Error fetching student details:', error);
        }
    };

    fetchStudentDetails();
}, []);

/*
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
*/

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
    }


    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email);


    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert(response.data.message);
      fetchFiles(); // Fetch files again after upload
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };


  const fetchFiles = useCallback(async () => {
    
      try {
        const response = await axios.get(`http://localhost:8080/files/${email}`);
        setFiles(response.data.files || []);
        console.log('Fetched files:', response.data); // Log fetched files
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    
  }, [email]);


  useEffect(() => {
    if (email) {
      fetchFiles(); // Fetch files when the email is not empty
    }
  }, [email, fetchFiles]);


  return (
    <div>
      <h2>Welcome, {student.name || 'Student'}</h2>
      <p>Total Marks: {student.marks || 0}</p>
      <form onSubmit={handleUpload}>
        <div>
          <label htmlFor="file">File:</label>
          <input
            type="file"
            onChange={handleFileChange}
            required
          />
        </div>
        <button type="submit">Upload File</button>
      </form>
      <h3>Your Uploaded Files</h3>
      <ul>
        {files.length > 0 ? (
          files.map(file => (
            <li key={file._id}>
              <a href={`http://localhost:8080/uploads/${file.filename}`} target="_blank" rel="noopener noreferrer">
                {file.originalname}
              </a>
            </li>
          ))
        ) : (
          <li>No files uploaded yet.</li>
        )}
      </ul>
    </div>
  );
}


export default StudentDashboard;
