import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function StudentDashboard() {
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);
  const { email } = useParams();
  const [student, setStudent] = useState({});
  const [view, setView] = useState("upload"); // "upload", "status", or "details"

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email);

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert(response.data.message);
      fetchFiles();
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const fetchFiles = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/files/${email}`);
      setFiles(response.data);
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  }, [email]);

  const fetchStudentDetails = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
      setStudent(response.data.student);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  }, [email]);

  const handleDelete = async (fileId) => {
    try {
      await axios.delete(`http://localhost:8080/files/${fileId}`);
      setFiles(files.filter(file => file._id !== fileId));
      alert('File deleted successfully');
    } catch (error) {
      console.error('Error deleting file:', error);
    }
  };

  useEffect(() => {
    if (email) {
      fetchFiles();
      fetchStudentDetails();
    }
  }, [email, fetchFiles, fetchStudentDetails]);

  return (
    <div className="container mt-5">
      <div className="card shadow-lg p-4 mb-5 bg-white rounded">
        <h2 className="text-center text-primary">Welcome, {student.name || 'Student'}</h2>
        <p className="text-center text-secondary mb-4">Total Marks: <strong>{student.marks || 0}</strong></p>

        {/* Dashboard Buttons */}
        <div className="button-group d-flex justify-content-around mb-4">
          <button onClick={() => setView("upload")} className="btn btn-outline-primary">Upload Certificates</button>
          <button onClick={() => setView("status")} className="btn btn-outline-info">View Status of Certificates</button>
          <button onClick={() => setView("details")} className="btn btn-outline-secondary">Details</button>
        </div>

        {/* Conditional Rendering based on selected view */}
        {view === "upload" && (
          <div className="upload-section">
            <h3>Upload Certificates</h3>
            <form onSubmit={handleUpload} className="mb-4">
              <div className="form-group">
                <label htmlFor="file" className="form-label">Upload File:</label>
                <input 
                  type="file" 
                  className="form-control" 
                  onChange={handleFileChange} 
                  required 
                />
              </div>
              <button type="submit" className="btn btn-primary w-100 mt-3">Upload File</button>
            </form>
          </div>
        )}

        {view === "status" && (
          <div className="status-section">
            <h3 className="text-center text-info">Your Uploaded Files</h3>

            {/* Pending Files Section */}
            <div className="mt-4">
              <h4 className="text-warning">Pending Files</h4>
              {files.filter(file => file.status === 'pending').map(file => (
                <div key={file._id} className="d-flex justify-content-between align-items-center my-2">
                  <a
                    href={`http://localhost:8080/uploads/${file.filename}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-outline-primary w-75"
                    style={{ textDecoration: 'none', textAlign: 'left' }}
                  >
                    {file.originalname}
                  </a>
                  <button
                    className="btn btn-danger ml-2"
                    onClick={() => handleDelete(file._id)}
                  >
                    Delete
                  </button>
                </div>
              ))}
            </div>

            {/* Approved Files Section */}
            <div className="mt-4">
              <h4 className="text-success">Approved Files</h4>
              {files.filter(file => file.status === 'approved').map(file => (
                <div key={file._id} className="d-flex justify-content-between align-items-center my-2">
                  <a
                    href={`http://localhost:8080/uploads/${file.filename}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-outline-success w-75"
                    style={{ textDecoration: 'none', textAlign: 'left' }}
                  >
                    {file.originalname}
                  </a>
                </div>
              ))}
            </div>

            {/* Rejected Files Section */}
            <div className="mt-4">
              <h4 className="text-danger">Rejected Files</h4>
              {files.filter(file => file.status === 'rejected').map(file => (
                <div key={file._id} className="d-flex justify-content-between align-items-center my-2">
                  <a
                    href={`http://localhost:8080/uploads/${file.filename}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-outline-danger w-75"
                    style={{ textDecoration: 'none', textAlign: 'left' }}
                  >
                    {file.originalname}
                  </a>
                </div>
              ))}
            </div>
          </div>
        )}

        {view === "details" && (
          <div className="details-section">
            <h3 className="text-center text-secondary">Certificate Details</h3>
            <table className="table table-bordered mt-3">
              <thead>
                <tr>
                  <th>Certificate Type</th>
                  <th>Minimum Points</th>
                  <th>Maximum Points</th>
                </tr>
              </thead>
              <tbody>
                {/* Static details - adjust as needed */}
                <tr>
                  <td>MOOCs (SWAYAM/NPTEL/COURSERA)</td>
                  <td>16</td>
                  <td>40</td>
                </tr>
                <tr>
                  <td>Tech Fest / R&D Day / Workshop</td>
                  <td>3</td>
                  <td>10</td>
                </tr>
                <tr>
                  <td>Participation in Relief Camps</td>
                  <td>20</td>
                  <td>40</td>
                </tr>
                {/* Add more rows as per requirement */}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default StudentDashboard;
