import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentDashboard() {
  const [files, setFiles] = useState([]);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [points, setPoints] = useState(0);
  const handleUpload = async (e) => {
    e.preventDefault(); // Prevent default form submission
    const formData = new FormData();
    formData.append('file', uploadedFile); // Append the selected file to form data
    console.log('Uploading file:', uploadedFile); // Log the uploaded file

    try {
      // Upload file (POST request to backend)
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data', // Set content type for file upload
        },
      });
      setFiles([...files, response.data]); // Update file list with the new file
      setUploadedFile(null); // Reset uploaded file state after successful upload
      console.log('File uploaded successfully:', response.data); // Log success message
    } catch (error) {
      console.error('Error uploading file', error); // Log error message
    }
  };


  useEffect(() => {
    // Fetch the student’s points and files (GET request to the backend)
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/student/files');
        setFiles(response.data.files);
        setPoints(response.data.points);
      } catch (error) {
        console.error('Error fetching student data', error);
      }
    };

    fetchData();
  }, []);

  const handleFileChange = (e) => {
    setUploadedFile(e.target.files[0]); // Set the selected file
  };

  
  return (
    <div>
      <h1>Student Dashboard</h1>
      <p>Total Points: {points}</p>

      <h2>Upload File</h2>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={e => setFiles(e.target.files[0])} required /> {/* Ensure a file is selected */}
        <button type="submit">Upload</button>
      </form>

      
    </div>
  );
}

export default StudentDashboard;
