import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import "./styles/StudentDashboard.css";
import 'bootstrap/dist/css/bootstrap.min.css';

function StudentDashboard() {
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);
  const { email } = useParams(); // Get email from URL parameters
  const [student, setStudent] = useState({});

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email);

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert(response.data.message);
      fetchFiles(); // Fetch files again after upload
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const fetchFiles = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/files/${email}`);
      setFiles(response.data); // Set the files directly if response.data is an array of files
      console.log('Fetched files:', response.data); // Log fetched files to see the structure
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  }, [email]);

  const fetchStudentDetails = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
      const studentData = response.data.student;
      setStudent(studentData);
      console.log('Fetched student details:', studentData);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  }, [email]);

  useEffect(() => {
    if (email) {
      fetchFiles(); // Fetch files when the email is not empty
      fetchStudentDetails();
    }
  }, [email, fetchFiles, fetchStudentDetails]);

  return (
    <div className="container mt-5">
      <div className="card shadow-lg p-4 mb-5 bg-white rounded">
        <h2 className="text-center text-primary">Welcome, {student.name || 'Student'}</h2>
        <p className="text-center text-secondary mb-4">Total Marks: <strong>{student.marks || 0}</strong></p>
        
        <form onSubmit={handleUpload} className="mb-4">
          <div className="form-group">
            <label htmlFor="file" className="form-label">Upload File:</label>
            <input 
              type="file" 
              className="form-control" 
              onChange={handleFileChange} 
              required 
            />
          </div>
          <button type="submit" className="btn btn-primary w-100 mt-3">Upload File</button>
        </form>

        <h3 className="text-center text-info">Your Uploaded Files</h3>
        <ul className="list-group list-group-flush mt-3">
          {files.length > 0 ? (
            files.map(file => (
              <li key={file._id} className="list-group-item">
                <a 
                  href={`http://localhost:8080/uploads/${file.filename}`} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="text-decoration-none text-primary">
                  {file.originalname}
                </a>
              </li>
            ))
          ) : (
            <li className="list-group-item text-secondary">No files uploaded yet.</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default StudentDashboard;
