import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function StudentDashboard() {
  const [student, setStudent] = useState({});
  const [file, setFile] = useState(null);
  const { email } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch student details
    const fetchStudentDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
        setStudent(response.data.student);
      } catch (error) {
        console.error('Error fetching student details:', error);
      }
    };

    fetchStudentDetails();
  }, [email]);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email);

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert(response.data.message);
      setFile(null); 
      navigate('/student/view-files');
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div >
      <div >
        <h2 className="text-center text-primary">Welcome, {student.name || 'Student'}</h2>
        <p className="text-center text-secondary mb-4">Total Marks: <strong>{student.marks || 0}</strong></p>

        <form onSubmit={handleUpload} className="mb-4">
          <div className="form-group">
            <label htmlFor="file" className="form-label">Upload File:</label>
            <input 
              type="file" 
              className="form-control" 
              onChange={handleFileChange} 
              required 
            />
          </div>
          <button type="submit" className="btn btn-primary w-100 mt-3">Upload File</button>
        </form>

        <div className="d-flex justify-content-around mt-4">
          <button
            className="btn btn-info"
            onClick={() => navigate('/student/${email}/view-files')}
          >
            View Uploaded Files
          </button>
          <button
            className="btn btn-warning"
            onClick={() => navigate('/student/viewdetails')}
          >
            View Details
          </button>
        </div>
      </div>
    </div>
  );
}

export default StudentDashboard;
