import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentDashboard() {
  const [file, setFile] = useState(null); // Define 'file' in the state
  const [email, setEmail] = useState('');
  const [files, setFiles] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };
  
  const handleEmailChange = (e) => {
    setEmail(e.target.value); // Capture the email input
    console.log("Current email:", e.target.value);
};

  

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
  }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email); // Add the email to the form data
 

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert(response.data.message);
      console.log(response.data);
      fetchFiles(); // Fetch files again after upload
    }catch (error) {
      // Add more detailed logging to understand the error
      if (error.response) {
        console.error('Error response:', error.response.data); // Log the error response data
        console.error('Error status:', error.response.status); // Log the status code
      } else {
        console.error('Error uploading file:', error.message); // Log generic error message
      }
    }
  };
  const fetchFiles = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/files/${email}`);
      setFiles(response.data);
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  useEffect(() => {
    if (email) {
      fetchFiles(); // Fetch files when the component mounts and email is available
    }
  }, [email, fetchFiles]);

  return (
    <div>
            <h2>Student Dashboard</h2>
            <form onSubmit={handleUpload}>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={handleEmailChange}
                        placeholder="Enter your email..."
                        required
                    />
                </div>
                <div>
                    <label htmlFor="file">File:</label>
                    <input
                        type="file"
                        onChange={handleFileChange}
                        required
                    />
                </div>
                <button type="submit">Upload File</button>
            </form>
            <ul>{files.map(file => (<li key={file._id}>{file.fileName}</li> // Adjust according to your file schema
            ))}
            </ul>
        </div>
    
  );
}

export default StudentDashboard;
