import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './styles/StudentDashboard.css';

function StudentDashboard() {
  const [student, setStudent] = useState({});
  const { email } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
        setStudent(response.data.student);
      } catch (error) {
        console.error('Error fetching student details:', error);
      }
    };
    fetchStudentDetails();
  }, [email]);

  return (
    <div className="student-dashboard">
      <div className="welcome-banner">
        <h2>Welcome, {student.name || 'Student'}!</h2>
        <p>Total Marks: <strong>{student.marks || 0}</strong></p>
      </div>

      <div className="button-group">
        <div 
          className="dashboard-button" 
          onClick={() => navigate(`/student/${email}/view-files`)}
        >
          <img src="https://www.shutterstock.com/image-vector/vector-icon-case-study-on-260nw-1395648677.jpg"/>
          <span>Uploaded Files</span>
        </div>
        <div 
          className="dashboard-button" 
          onClick={() => navigate('/student/viewdetails')}
        >
          <img src="https://www.shutterstock.com/image-vector/evaluation-criteria-icon-line-vector-600nw-2445089389.jpg" alt="View Details" className="icon"/>
          <span>View Details</span>
        </div>
        <div 
          className="dashboard-button" 
          onClick={() => navigate(`/student/${email}/upload`)}
        >
          <img src="https://thumbs.dreamstime.com/b/file-upload-glyphs-icon-file-upload-glyphs-icon-elements-mobile-concept-web-apps-thin-line-icons-website-design-104430672.jpg" alt="Upload File" className="icon"/>
          <span>File Upload</span>
        </div>
      </div>
    </div>
  );
}

export default StudentDashboard;
