import React, { useState, useEffect } from 'react';
import axios from 'axios';

function MentorDashboard() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    // Fetch files that need approval (GET request to backend)
    const fetchFiles = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/mentor/files');
        setFiles(response.data.files);
      } catch (error) {
        console.error('Error fetching files', error);
      }
    };

    fetchFiles();
  }, []);

  const handleApprove = async (fileId) => {
    try {
      await axios.post(`http://localhost:8080/api/mentor/approve/${fileId}`);
      setFiles(files.filter(file => file.id !== fileId)); // Remove approved file from list
    } catch (error) {
      console.error('Error approving file', error);
    }
  };

  const handleReject = async (fileId) => {
    try {
      await axios.post(`http://localhost:8080/api/mentor/reject/${fileId}`);
      setFiles(files.filter(file => file.id !== fileId)); // Remove rejected file from list
    } catch (error) {
      console.error('Error rejecting file', error);
    }
  };

  return (
    <div>
      <h1>Mentor Dashboard</h1>
      <h2>Files for Approval</h2>
      <ul>
        {files.map((file) => (
          <li key={file.id}>
            {file.name}
            <button onClick={() => handleApprove(file.id)}>Approve</button>
            <button onClick={() => handleReject(file.id)}>Reject</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MentorDashboard;
