import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';

function StudentDashboard() {
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);
  const { email } = useParams(); // Get email from URL parameters
  const [student, setStudent] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    if (email) {
      fetchFiles(); // Fetch files when the email is not empty
      fetchStudentDetails();
    }
  }, [email, fetchFiles, fetchStudentDetails]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !email) {
      alert('Please select a file and enter your email!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('email', email);

    try {
      const response = await axios.post('http://localhost:8080/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert(response.data.message);
      fetchFiles(); // Fetch files again after upload
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const fetchFiles = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/files/${email}`);
      setFiles(response.data); // Set the files directly if response.data is an array of files
      console.log('Fetched files:', response.data); // Log fetched files to see the structure
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  }, [email]);

  const fetchStudentDetails = useCallback(async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/mentor/student/${email}/details`);
      const studentData = response.data.student;
      setStudent(studentData);
      console.log('Fetched student details:', studentData);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  }, [email]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('loggedInUser');
    handleSuccess('User Logged out');
    setTimeout(() => {
      navigate('/');
    }, 1000);
  };

  return (
    <div>
      <h2>Welcome, {student.name || 'Student'}</h2>
      <button onClick={handleLogout}>Logout</button>
      <ToastContainer />
      <p>Total Marks: {student.marks || 0}</p>
      <form onSubmit={handleUpload}>
        <div>
          <label htmlFor="file">File:</label>
          <input type="file" onChange={handleFileChange} required />
        </div>
        <button type="submit">Upload File</button>
      </form>
      <h3>Your Uploaded Files</h3>
      <ul>
        {files.length > 0 ? (
          files.map(file => (
            <li key={file._id}>
              <a href={`http://localhost:8080/uploads/${file.filename}`} target="_blank" rel="noopener noreferrer">
                {file.originalname}
              </a>
            </li>
          ))
        ) : (
          <li>No files uploaded yet.</li>
        )}
      </ul>
    </div>
  );
}

export default StudentDashboard;
