import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate,useParams } from 'react-router-dom';
import './styles/MentorDashboard.css';

function MentorDashboard() {
  const [mentor, setMentor] = useState({});
  const [students, setStudents] = useState([]);
  const [loggedInUser, setLoggedInUser] = useState('');
  const [isProfileOpen, setIsProfileOpen] = useState(false); // State to toggle profile info visibility
  const navigate = useNavigate();
  const { email } = useParams(); 

  useEffect(() => {
    const fetchMentorDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/mentor/mentor/${email}/details`);
        if (response.data && response.data.mentor) {
          setMentor(response.data.mentor);
        } else {
          console.error('Mentor data not found in response');
        }
      } catch (error) {
        console.error('Error fetching mentor details:', error);
      }
    };
    fetchMentorDetails();
    
    setLoggedInUser(localStorage.getItem('loggedInUser'));
    
    const fetchStudents = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/mentor/students');
        setStudents(response.data);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };
    fetchStudents();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('loggedInUser');
    setTimeout(() => {
      navigate('/');
    }, 1000);
  };

  const toggleProfileInfo = () => {
    setIsProfileOpen(!isProfileOpen); // Toggle profile info visibility
  };

  const handleStudentClick = (email) => {
    navigate(`/verification/${email}`);
  };

  return (
    <div className="mentor-dashboard">
      {/* Welcome Banner */}
      <div className="welcome-banner">
        <h2 style={{ color: "#262d41" }}>Welcome, {mentor.name || 'Mentor'}!</h2>
        <p>Total Students: <strong>{students.length || 0}</strong></p>
        
        {/* Profile Icon and Logout Button Container */}
        <div className="header-buttons">
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>

          <div className="profile-icon">
            <img
              src="https://t4.ftcdn.net/jpg/08/19/66/31/360_F_819663119_che4sZSrmQv8uQJOzuN9TVQFQNHJlfQ2.jpg"
              alt="Profile Icon"
              onClick={toggleProfileInfo} // Toggle profile info visibility on click
            />
            {isProfileOpen && (
              <div className="profile-info">
                <p className="profile-detail">Name: {mentor.name || 'Mentor'}</p>
                <p className="profile-detail">Role: {loggedInUser ? 'Mentor' : 'Guest'}</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Scrollable list of students */}
      <div className="student-list">
        <h3>Registered Students</h3>
        {students.length === 0 ? (
          <p>No students registered yet.</p>
        ) : (
          <ul>
            {students.map((student) => (
              <li
                key={student.email}
                onClick={() => handleStudentClick(student.email)}
                className="student-item"
              >
                {student.name} - {student.email}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default MentorDashboard;
