import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentDashboard() {
  const [files, setFiles] = useState([]);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [points, setPoints] = useState(0);

  useEffect(() => {
    // Fetch the student’s points and files (GET request to the backend)
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/student/files');
        setFiles(response.data.files);
        setPoints(response.data.points);
      } catch (error) {
        console.error('Error fetching student data', error);
      }
    };

    fetchData();
  }, []);

  const handleFileChange = (e) => {
    setUploadedFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', uploadedFile);

    try {
      // Upload file (POST request to backend)
      const response = await axios.post('http://localhost:8080/api/student/upload', formData);
      setFiles([...files, response.data.file]); // Update file list
    } catch (error) {
      console.error('Error uploading file', error);
    }
  };

  return (
    <div>
      <h1>Student Dashboard</h1>
      <p>Total Points: {points}</p>

      <h2>Upload File</h2>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>

      <h2>Uploaded Files</h2>
      <ul>
        {files.map((file) => (
          <li key={file.id}>{file.name} - {file.status}</li>
        ))}
      </ul>
    </div>
  );
}

export default StudentDashboard;
