import { Navigate, Route, Routes } from 'react-router-dom';
import './App.css';
import Login from './pages/Login';
import Signup from './pages/Signup';
import MentorLogin from './pages/MentorLogin';
import MentorSignup from './pages/MentorSignup';
import LandingPage from './pages/Landingpage'; 
import { useState } from 'react';
import RefrshHandler from './RefreshHandler';
import StudentDashboard from './pages/StudentDashboard';  // Import StudentDashboard
import MentorDashboard from './pages/MentorDashboard';  

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loggedInEmail, setLoggedInEmail] = useState('');

  const handleLogin = (email) => {
    setIsAuthenticated(true);
    setLoggedInEmail(email);
  };

  const PrivateRoute = ({ element }) => {
    return isAuthenticated ? element : <Navigate to="/login" />
  }

  return (

    <div className="App">
      <RefrshHandler setIsAuthenticated={setIsAuthenticated} />
      <Routes>
        <Route path='/' element={<LandingPage />} />
        <Route path='/login' element={<Login loggedInEmail={handleLogin} />} />
        <Route path='/signup' element={<Signup />} />
        <Route path='/mentorlogin' element={<MentorLogin />} />
        <Route path='/mentorsignup' element={<MentorSignup />} />
        <Route path="/student/dashboard" element={<PrivateRoute element={<StudentDashboard loggedInEmail={loggedInEmail} setIsAuthenticated={setIsAuthenticated} />} />} />

        <Route path='/mentor/dashboard' element={<MentorDashboard />} />
        <Route path="*" element={<Navigate to="/" />} />


      </Routes>
    </div>
  );
}

export default App;
